// App.js
import React, { useState, useEffect } from 'react';
import CalendarComponent from './CalendarComponent';
import LoginModal from './LoginModal';
import BookingList from './BookingList';
import { auth } from './firebaseConfig';

const App = () => {
  const [user, setUser] = useState(null);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [selectedDate, setSelectedDate] = useState(null);

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
    });
    return unsubscribe;
  }, []);

  const handleDateClick = (date) => {
    if (!user) {
      setShowLoginModal(true);
      setSelectedDate(date);
    } else {
      // ユーザーがログイン済みの場合、予約を直接処理します
      bookReservation(date);
    }
  };

  const bookReservation = (date) => {
    // 予約処理のロジック（例：バックエンドへのAPI呼び出し）
    console.log('予約が完了しました:', date);
  };

  return (
    <div>
      <CalendarComponent onDateClick={handleDateClick} />
      {user && <BookingList user={user} />}
      {showLoginModal && (
        <LoginModal 
          setShowLoginModal={setShowLoginModal} 
          onLogin={() => {
            setShowLoginModal(false);
            bookReservation(selectedDate);
          }}
        />
      )}
    </div>
  );
};

export default App;
